import React from 'react';

const Form = ({onInputChange}) => {
	return(
		<div>
			<p className='f3'>Enter the text you want to be translated.
			</p>
			<div className="pa4">
				<div className='br3 pa4 shadow-5'>
					<input className='f4 pa2 w-70 center' type="text" name="usertext" onChange={onInputChange}/>
					<button style={{opacity:0.9}} className='w-30 grow  pa1 f4 link ph3 pv2 dib white bg-light-purple'>Translate</button>
				</div>
			</div>
		</div>
		);
};

export default Form;
